function append(array) {
  $("#region").change(function () {
    var regionVal = $(this).val();
    var strResult = "<option>" + "來去看看吧!" + "</option>";
    var str = "<option>" + "暫無資料" + "</option>";
    $("#name").html(str);
    $("#go").click(function () {
      $("#name").html(str);
      for (i = 0; i < resultAddr.length; i++) {
        if (resultAddr[i].Town == regionVal && resultAddr[i].Region == array) {
          strResult = strResult + "<option>" + resultAddr[i].Name + "</option>";
          $("#name").html(strResult);
        }
      }
    });
  });
}

