$("#name").change(function () {
  var nameVal = $(this).val();
  var nameStr = "";
  for (i = 1; i < resultAddr.length; i++) {
    if (resultAddr[i].Name == "來去看看吧!") {
      nameStr = "";
    } else if (resultAddr[i].Name == nameVal) {
      nameStr =
        nameStr +
        resultAddr[i].Name +
        "<br>" +
        "電話: " +
        resultAddr[i].Tel +
        "<br>" +
        "門票: " +
        resultAddr[i].Ticketinfo +
        "<br>" +
        "簡介: " +
        resultAddr[i].Description;
      $("#description").show();
      $("#description").html(nameStr);
    }
  }
});
